mapper.select:
SELECT inst.ID_ AS id, inst.PROC_DEF_ID_ AS processId, flow_definition.name AS name, inst.START_USER_ID_ AS started, inst.START_TIME_ AS startedTime, inst.END_TIME_ AS endTime, inst.STATE AS instanceState, task.ID_ AS taskId, task.NAME_ AS taskName, task.ASSIGNEE_ AS taskAssignee, flow_version.titleHtml, node.formId AS formId, flow_definition.icon, flow_definition.listIcon AS listIcon FROM ACT_HI_PROCINST AS inst LEFT JOIN ACT_RE_PROCDEF AS proc ON inst.PROC_DEF_ID_ = proc.ID_ LEFT JOIN ACT_RU_TASK AS task ON inst.ID_ = task.PROC_INST_ID_ LEFT JOIN flow_version ON flow_version.id = SUBSTRING_INDEX(inst.PROC_DEF_ID_, ':', 1 ) LEFT JOIN flow_definition ON flow_definition.id = flow_version.definitionId LEFT JOIN flow_node node ON flow_version.id = node.processId AND flow_version.startedNodeId = node.id WHERE (flow_definition.isTest = 0 OR flow_definition.isTest IS NULL) AND inst.SUPER_PROCESS_INSTANCE_ID_ IS NULL AND (task.ID_ = (select max(task.ID_) from ACT_RU_TASK where inst.ID_ = task.PROC_INST_ID_) OR task.ID_ IS NULL)
AND flow_definition.name LIKE '%${filters.processName}%'
AND inst.START_USER_ID_ = #{filters.startedUserId}
AND inst.START_USER_ID_ LIKE '%${filters.startedUserName}%'
AND (inst.STATE = #{filters.processState} OR (#{filters.processState} = 1 AND inst.STATE IS NULL))
AND inst.START_TIME_ >= #{filters.startedAfter}
AND inst.START_TIME_ <= #{filters.startedBefore}
GROUP BY inst.ID_, flow_definition.`name`, flow_version.titleHtml, node.formId, task.ID_ ORDER BY inst.START_TIME_ DESC LIMIT #{start}, #{size}

SELECT COUNT(*) FROM ACT_HI_PROCINST AS inst LEFT JOIN ACT_RE_PROCDEF AS proc ON inst.PROC_DEF_ID_ = proc.ID_ LEFT JOIN ACT_RU_TASK AS task ON inst.ID_ = task.PROC_INST_ID_ LEFT JOIN flow_version ON flow_version.id = SUBSTRING_INDEX(inst.PROC_DEF_ID_, ':', 1 ) LEFT JOIN flow_definition ON flow_definition.id = flow_version.definitionId WHERE (flow_definition.isTest = 0 OR flow_definition.isTest IS NULL) AND inst.SUPER_PROCESS_INSTANCE_ID_ IS NULL AND (task.ID_ = (select max(task.ID_) from ACT_RU_TASK where inst.ID_ = task.PROC_INST_ID_) OR task.ID_ IS NULL)
AND flow_definition.name LIKE '%${filters.processName}%'
AND inst.START_USER_ID_ = #{filters.startedUserId}
AND inst.START_USER_ID_ LIKE '%${filters.startedUserName}%'
AND (inst.STATE = #{filters.processState} OR (#{filters.processState} = 1 AND inst.STATE IS NULL))
AND inst.START_TIME_ >= #{filters.startedAfter}
AND inst.START_TIME_ <= #{filters.startedBefore}
